#include "tb.h"
#include "tb_rmi_features.h"

bool tb_rmi_features(
	uint64_t index)
{
	/*
	 * Initialize registers
	 */

	struct tb_regs __tb_regs = __tb_arb_regs();

	__tb_regs.X0 = SMC_RMM_FEATURES;
	__tb_regs.X1 = (uint64_t)index;

	/*
	 * Initialize global state
	 */

	__init_global_state(__tb_regs.X0);

	/*
	 * Pre-conditions
	 */

	bool no_failures_pre = true;
	bool success_index_pre = index != 0;

	/*
	 * Execute command and read the result.
	 */

	tb_handle_smc(&__tb_regs);
	uint64_t result = __tb_regs.X0;
	uint64_t value = __tb_regs.X1;

	/*
	 * Post-conditions
	 */

	bool success_index_post = value == Zeros();

	/*
	 * Result assertion
	 */

	bool prop_result_ante = no_failures_pre;

	__COVER(prop_result_ante);
	if (prop_result_ante) {
		bool prop_result_cons = result == RMI_SUCCESS;

		__COVER(prop_result_cons);
		__ASSERT(prop_result_cons, "prop_result_cons");
	}

	/*
	 * Success condition assertions
	 */

	bool prop_success_index_ante = no_failures_pre
		&& success_index_pre;

	__COVER(prop_success_index_ante);
	if (prop_success_index_ante) {
		bool prop_success_index_cons = success_index_post
		&& (result == RMI_SUCCESS);

		__COVER(prop_success_index_cons);
		__ASSERT(prop_success_index_cons, "prop_success_index_cons");
	}

	/*
	 * Assertion used to check consistency of the testbench
	 */
	__tb_expect_fail();

	return no_failures_pre;
}
